mapper.select:
  		select
			a.fgSeq
		    ,a.fgName
		    ,a.fgPrice
		    ,a.fgStockQty
		    ,a.fgRegDate
		    ,a.fgModDate
		    ,a.fgDelNy
		    ,a.fgUseNy
		from
			freegift a
		where 1=1
			AND a.fgDelNy = #{sDelNy}
			AND a.fgUseNy = #{sUseNy}


					AND a.fgStockQty >	 0
					AND a.fgStockQty = 0





					AND a.fgSeq = #{searchKeyword}
					AND a.fgName Like CONCAT('%',#{searchKeyword},'%') 



				 AND a.fgRegDate BETWEEN #{dateStart} AND #{dateEnd}
				 AND a.fgModDate BETWEEN #{dateStart} AND #{dateEnd}



			        AND a.fgPrice BETWEEN #{minPrice} AND #{maxPrice}


			        AND a.fgPrice >= #{minPrice} 


			        AND a.fgPrice <= #{maxPrice}


		order by a.fgSeq desc

mapper.update:
UPDATE freegift
SET
	fgName = #{fgName}
	,fgPrice = #{fgPrice}
	,fgStockQty = #{fgStockQty}
	,freeGiftInfo = #{freeGiftInfo}
	,fgUseNy = #{fgUseNy}

	,fgModDate = now()
WHERE 1=1
	AND fgSeq = #{fgSeq}

mapper.delete:
delete from freegift
WHERE 1=1
	AND fgSeq = #{fgSeq}
