mapper.select:
SELECT a.seq ,a.codeGroupName ,a.groupOrder ,a.groupDesc ,a.useNy ,a.delNy ,a.regDate ,a.modDate FROM codegroup a WHERE 1=1
AND a.codeGroupName LIKE CONCAT('%', #{searchKeyword}, '%')
ORDER BY a.groupOrder desc LIMIT #{limit} OFFSET #{offset}

mapper.update:
UPDATE codegroup
SET
	codeGroupName = #{codeGroupName}
	,groupOrder = #{groupOrder}
	,groupDesc = #{groupDesc}
	,useNy = #{useNy}
	,delNy = #{delNy}
	,modDate = now()
WHERE 1=1
	AND seq = #{seq}
