mapper.select:
SELECT a.seq ,a.codeName ,a.codeOrder ,a.codeDesc ,b.codeGroupName ,a.useNy ,a.delNy ,a.regDate ,a.modDate ,a.codeGroup_seq FROM commoncode a left join codegroup b on a.codeGroup_seq = b.seq where 1=1 AND a.delNy = #{sDelNy} AND a.useNy = #{sUseNy}
AND( a.seq Like CONCAT('%',#{searchKeyword},'%') OR b.codeGroupName Like CONCAT('%',#{searchKeyword},'%') OR a.codeName Like CONCAT('%',#{searchKeyword},'%') )
AND a.seq = #{searchKeyword} AND b.codeGroupName Like CONCAT('%',#{searchKeyword},'%') AND a.codeName Like CONCAT('%',#{searchKeyword},'%')
AND a.regDate BETWEEN #{dateStart} AND #{dateEnd} AND a.modDate BETWEEN #{dateStart} AND #{dateEnd}
ORDER BY
a.codeGroup_seq desc , a.codeOrder desc

SELECT a.seq ,a.codeName ,a.codeOrder ,a.codeDesc ,b.codeGroupName ,a.useNy ,a.delNy ,a.regDate ,a.modDate ,a.codeGroup_seq FROM commoncode a left join codegroup b on a.codeGroup_seq = b.seq WHERE 1=1 AND a.delNy = #{sDelNy} AND a.useNy = #{sUseNy}
AND( a.seq Like CONCAT('%',#{searchKeyword},'%') OR b.codeGroupName Like CONCAT('%',#{searchKeyword},'%') OR a.codeName Like CONCAT('%',#{searchKeyword},'%') )
AND a.seq = #{searchKeyword} AND b.codeGroupName Like CONCAT('%',#{searchKeyword},'%') AND a.codeName Like CONCAT('%',#{searchKeyword},'%')
AND a.regDate BETWEEN #{dateStart} AND #{dateEnd} AND a.modDate BETWEEN #{dateStart} AND #{dateEnd}
ORDER BY a.codeGroup_seq desc , a.codeOrder desc LIMIT #{limit} OFFSET #{offset}

        SELECT COUNT(*) 
        FROM 
			commoncode a
			join codegroup b on a.codeGroup_seq = b.seq
        WHERE 1=1
			AND a.delNy = #{sDelNy}
			AND a.useNy = #{sUseNy}


					AND(
						a.seq Like CONCAT('%',#{searchKeyword},'%')
						OR b.codeGroupName Like CONCAT('%',#{searchKeyword},'%')
						OR a.codeName Like CONCAT('%',#{searchKeyword},'%')
					)

				AND a.seq = #{searchKeyword}
				AND b.codeGroupName Like CONCAT('%',#{searchKeyword},'%') 
				AND a.codeName Like CONCAT('%',#{searchKeyword},'%')


				 AND a.regDate BETWEEN #{dateStart} AND #{dateEnd}
				 AND a.modDate BETWEEN #{dateStart} AND #{dateEnd}

mapper.update:
UPDATE commoncode
SET
	codeName = #{codeName}
	,codeOrder = #{codeOrder}
	,codeDesc = #{codeDesc}
	,useNy = #{useNy}
	,codeGroup_seq = #{codeGroup_seq}
	,modDate = now()
WHERE 1=1
	AND seq = #{seq}

mapper.delete:
delete from commoncode
WHERE 1=1
	AND seq = #{seq}
