mapper.select:
SELECT a.seq ,a.codeName ,a.codeOrder ,a.codeDesc ,b.codeGroupName ,a.useNy ,a.delNy ,a.regDate ,a.modDate ,a.codeGroup_seq FROM commoncode a left join codegroup b on a.codeGroup_seq = b.seq WHERE 1=1
AND a.codeName LIKE CONCAT('%', #{searchKeyword}, '%')
ORDER BY a.codeGroup_seq desc , a.codeOrder desc LIMIT #{limit} OFFSET #{offset}

mapper.update:
UPDATE commoncode
SET
	codeName = #{codeName}
	,codeOrder = #{codeOrder}
	,codeDesc = #{codeDesc}
	,useNy = #{useNy}
	,codeGroup_seq = #{codeGroup_seq}
	,modDate = now()
WHERE 1=1
	AND seq = #{seq}

mapper.delete:
delete from commoncode
WHERE 1=1
	AND seq = #{seq}
