mapper.sql:
FROM
	commoncode a
	left join codegroup b on a.codeGroup_seq = b.seq
where 1=1
	AND a.delNy = #{sDelNy}
	AND a.useNy = #{sUseNy}



				AND(
					a.seq Like CONCAT('%',#{searchKeyword},'%')
					OR b.codeGroupName Like CONCAT('%',#{searchKeyword},'%')
					OR a.codeName Like CONCAT('%',#{searchKeyword},'%')
				)

			AND a.seq = #{searchKeyword}
			AND b.codeGroupName Like CONCAT('%',#{searchKeyword},'%') 
			AND a.codeName Like CONCAT('%',#{searchKeyword},'%')



		 AND a.regDate BETWEEN #{dateStart} AND #{dateEnd}
		 AND a.modDate BETWEEN #{dateStart} AND #{dateEnd}

mapper.update:
UPDATE commoncode
SET
	codeName = #{codeName}
	,codeOrder = #{codeOrder}
	,codeDesc = #{codeDesc}
	,useNy = #{useNy}
	,codeGroup_seq = #{codeGroup_seq}
	,modDate = now()
WHERE 1=1
	AND seq = #{seq}

mapper.delete:
delete from commoncode
WHERE 1=1
	AND seq = #{seq}
