mapper.sql:
  		from
			deliveryaddress a
			join member b on b.seq = a.member_seq
		where 1=1
			AND a.delNy = #{sDelNy}
			AND a.defaultNy = #{defaultNy}



						AND(
							a.name Like CONCAT('%',#{searchKeyword},'%')
							OR a.userId Like CONCAT('%',#{searchKeyword},'%')
							OR a.userPassword Like CONCAT('%',#{searchKeyword},'%')
							OR a.email Like CONCAT('%',#{searchKeyword},'%')
						)

					AND a.name = #{searchKeyword}
					AND a.userId Like CONCAT('%',#{searchKeyword},'%') 
					AND a.userPassword Like CONCAT('%',#{searchKeyword},'%')
					AND a.email Like CONCAT('%',#{searchKeyword},'%')




					 AND a.regDate BETWEEN #{dateStart} AND #{dateEnd}
					 AND a.modDate BETWEEN #{dateStart} AND #{dateEnd}

mapper.insert:
  		insert into deliveryaddress(
			defaultNy
		    ,addressName
		    ,RecipientName
		    ,RecipientPhone
		    ,daZonecode
		    ,daRoadAddress
		    ,daJibunAddress
		    ,daDetailAddress
		    ,daExtraAddress
		    ,longitude
		    ,latitude
		    ,regDate
		    ,modDate
		    ,delNy
		    ,member_seq

		)value(
			#{defaultNy}
			,#{addressName}
		    ,#{RecipientName}
		    ,#{RecipientPhone}
		    ,#{daZonecode}
		    ,#{daRoadAddress}
		    ,#{daJibunAddress}
		    ,#{daDetailAddress}
		    ,#{daExtraAddress}
		    ,#{longitude}
		    ,#{latitude}
		    ,now()
		    ,now()
		    ,0
		    ,#{member_seq}
		)

			SELECT last_insert_id()

mapper.update:
UPDATE deliveryaddress
SET
	defaultNy = #{defaultNy}
	,addressName = #{addressName}
	,RecipientName = #{RecipientName}
	,RecipientPhone = #{RecipientPhone}
	,daZonecode = #{daZonecode}
    ,daRoadAddress = #{daRoadAddress}
    ,daJibunAddress = #{daJibunAddress}
    ,daDetailAddress = #{daDetailAddress}
    ,daExtraAddress = #{daExtraAddress}
    ,longitude = #{longitude}
	,latitude = #{latitude}
	,modDate = now()
WHERE 1=1
	AND daSeq = #{daSeq}

UPDATE deliveryaddress
SET
	defaultNy = #{defaultNy}
	,modDate = now()
WHERE 1=1
	AND daSeq = #{daSeq}

UPDATE deliveryaddress
SET
	defaultNy = #{defaultNy}
WHERE 1=1
	AND daSeq = #{daSeq}
