  	where 1=1
		AND a.delNy = #{sDelNy}
		AND a.orderStatus = #{orderStatus}



					AND(
						a.orderNumber Like CONCAT('%',#{searchKeyword},'%')
						OR d.title Like CONCAT('%',#{searchKeyword},'%')
						OR b.name Like CONCAT('%',#{searchKeyword},'%')
					)

				AND a.orderNumber Like CONCAT('%',#{searchKeyword},'%')
				AND b.name Like CONCAT('%',#{searchKeyword},'%') 
				AND d.title Like CONCAT('%',#{searchKeyword},'%') 



			 AND a.orderDate BETWEEN #{dateStart} AND #{dateEnd}
			 AND a.uoModDate BETWEEN #{dateStart} AND #{dateEnd}
			 AND a.deliveryDate BETWEEN #{dateStart} AND #{dateEnd}



		        AND a.uoPrice BETWEEN #{minPrice} AND #{maxPrice}


		        AND a.uoPrice >= #{minPrice} 


		        AND a.uoPrice <= #{maxPrice}
