mapper.sql:
  	where 1=1
			AND prodType.codeGroup_seq = 12
			AND publisher.codeGroup_seq = 3

			AND a.delNy = #{sDelNy}
			AND a.bestNy = #{bestNy}
			AND a.todayPickNy = #{todayPickNy}
			AND a.prodType = #{prodType}
			AND a.freeShippingNy = #{freeShippingNy}
			AND a.taxDeductionNy = #{taxDeductionNy}
			AND a.mdPickNy = #{mdPickNy}



						AND(
							a.seq Like CONCAT('%',#{searchKeyword},'%')
							OR a.title Like CONCAT('%',#{searchKeyword},'%')
							OR b.name Like CONCAT('%',#{searchKeyword},'%')
							OR publisher.codeName Like CONCAT('%',#{searchKeyword},'%')
						)

					AND a.seq = #{searchKeyword}
					AND a.title Like CONCAT('%',#{searchKeyword},'%') 
					AND b.name Like CONCAT('%',#{searchKeyword},'%')
					AND publisher.codeName Like CONCAT('%',#{searchKeyword},'%')





					 AND a.regDate BETWEEN #{dateStart} AND #{dateEnd}
					 AND a.modDate BETWEEN #{dateStart} AND #{dateEnd}
					 AND a.makeDate BETWEEN #{dateStart} AND #{dateEnd}
					 AND a.shipDate BETWEEN #{dateStart} AND #{dateEnd}




			        AND a.salePrice BETWEEN #{minPrice} AND #{maxPrice}




			        AND a.reviewNum BETWEEN #{minReview} AND #{maxReview}




				 AND a.makeDate BETWEEN DATE_SUB(NOW(), INTERVAL 1 MONTH) AND NOW()
				 AND a.makeDate BETWEEN DATE_SUB(NOW(), INTERVAL 3 MONTH) AND NOW()
				 AND a.makeDate BETWEEN DATE_SUB(NOW(), INTERVAL 6 MONTH) AND NOW()
				 AND a.makeDate BETWEEN DATE_SUB(NOW(), INTERVAL 12 MONTH) AND NOW()
				 AND a.makeDate BETWEEN DATE_SUB(NOW(), INTERVAL 36 MONTH) AND NOW()
				 AND a.makeDate < DATE_SUB(NOW(), INTERVAL 36 MONTH)

mapper.insert:
insert into product(
	title
    ,prodType
    ,publisher
    ,makeDate
    ,price
    ,sale
    ,salePrice
    ,point
    ,stockQty
    ,intro
    ,reviewNum
    ,shipDate
    ,tableOfContents
    ,publisherReview
    ,preivew
    ,viewNumber
    ,buyNumber
    ,bestNy
    ,todayPickNy
    ,mdPickNy
    ,freeShippingNy
    ,taxDeductionNy
    ,sliderText
    ,regDate
    ,modDate
    ,delNy
) value(
	#{title}
    ,#{prodType}
    ,#{publisher}
    ,#{makeDate}
    ,#{price}
    ,#{sale}
    ,#{salePrice}
    ,#{point}
    ,#{stockQty}
    ,#{intro}
    ,#{reviewNum}
    ,#{shipDate}
    ,#{tableOfContents}
	,#{publisherReview}
    ,#{preivew}
    ,0
    ,0
    ,#{bestNy}
    ,#{todayPickNy}
    ,#{mdPickNy}
    ,#{freeShippingNy}
    ,#{taxDeductionNy}
    ,#{sliderText}
    ,now()
    ,now()
    ,0
)

	SELECT last_insert_id()

mapper.update:
  	UPDATE product
	SET
		title = #{title}
	    ,prodType = #{prodType}
	    ,publisher = #{publisher}
	    ,makeDate = #{makeDate}
	    ,price = #{price}
	    ,sale = #{sale}
	    ,salePrice = #{salePrice}
	    ,point = #{point}
	    ,stockQty = #{stockQty}
	    ,intro = #{intro}
	    ,reviewNum = #{reviewNum}
	    ,shipDate = #{shipDate}
	    ,tableOfContents = #{tableOfContents}
	    ,publisherReview = #{publisherReview}
	    ,preivew = #{preivew}
	    ,bestNy = #{bestNy}
	    ,todayPickNy = #{todayPickNy}
	    ,mdPickNy = #{mdPickNy}
	    ,freeShippingNy = #{freeShippingNy}
	    ,taxDeductionNy = #{taxDeductionNy}
	    ,sliderText = #{sliderText}
		,modDate = now()
	WHERE 1=1
		AND seq = #{seq}

mapper.delete:
delete from product
WHERE 1=1
	AND seq = #{seq}
