mapper.update:
  	UPDATE product
	SET
		title = #{title}
	    ,prodType = #{prodType}
	    ,publisher = #{publisher}
	    ,makeDate = #{makeDate}
	    ,price = #{price}
	    ,sale = #{sale}
	    ,salePrice = #{salePrice}
	    ,point = #{point}
	    ,stockQty = #{stockQty}
	    ,intro = #{intro}
	    ,reviewNum = #{reviewNum}
	    ,shipDate = #{shipDate}
	    ,tableOfContents = #{tableOfContents}
	    ,publisherReview = #{publisherReview}
	    ,preivew = #{preivew}
	    ,bestNy = #{bestNy}
	    ,todayPickNy = #{todayPickNy}
	    ,sliderText = #{sliderText}]
		,modDate = now()
	WHERE 1=1
		AND seq = #{seq}
