mapper.select:
select a.seq
,a.title ,a.prodType ,b.name ,a.publisher ,a.makeDate ,a.price ,a.sale ,a.salePrice ,a.point ,a.stockQty ,a.imgSrc ,a.intro ,a.reviewNum ,a.shipDate ,a.tableOfContents ,a.publisherReview ,a.preivew ,a.viewNumber ,a.buyNumber ,a.bestNy ,a.todayPickNy ,a.sliderText ,a.regDate ,a.modDate ,a.delNy ,prodType.codeName as prodTypeName ,publisher.codeName as publisherName from product a left join productAuthor c on c.product_seq = a.seq left join author b on c.author_seq = b.seq left join commoncode prodType on a.prodType = prodType.seq left join commoncode publisher on a.publisher = publisher.seq where 1=1 AND prodType.codeGroup_seq = 12 AND publisher.codeGroup_seq = 3 AND a.delNy = #{sDelNy} AND a.bestNy = #{bestNy} AND a.todayPickNy = #{todayPickNy} AND a.prodType = #{prodType}
AND( a.seq Like CONCAT('%',#{searchKeyword},'%') OR a.title Like CONCAT('%',#{searchKeyword},'%') OR b.name Like CONCAT('%',#{searchKeyword},'%') OR publisher.codeName Like CONCAT('%',#{searchKeyword},'%') )
AND a.seq = #{searchKeyword} AND a.title Like CONCAT('%',#{searchKeyword},'%') AND b.name Like CONCAT('%',#{searchKeyword},'%') AND publisher.codeName Like CONCAT('%',#{searchKeyword},'%')
AND a.regDate BETWEEN #{dateStart} AND #{dateEnd} AND a.modDate BETWEEN #{dateStart} AND #{dateEnd} AND a.makeDate BETWEEN #{dateStart} AND #{dateEnd} AND a.shipDate BETWEEN #{dateStart} AND #{dateEnd}
order by a.seq desc

select a.seq ,a.title ,a.prodType ,b.name ,a.publisher ,a.makeDate ,a.price ,a.sale ,a.salePrice ,a.point ,a.stockQty ,a.imgSrc ,a.intro ,a.reviewNum ,a.shipDate ,a.tableOfContents ,a.publisherReview ,a.preivew ,a.viewNumber ,a.buyNumber ,a.bestNy ,a.todayPickNy ,a.sliderText ,a.regDate ,a.modDate ,a.delNy from product a left join productAuthor c on c.product_seq = a.seq left join author b on c.author_seq = b.seq join commoncode d on b.authorType = d.seq where 1=1 AND a.prodType = #{prodType}
AND a.salePrice BETWEEN #{minPrice} AND #{maxPrice}
AND a.reviewNum BETWEEN #{minReview} AND #{maxReview}
AND a.makeDate BETWEEN DATE_SUB(NOW(), INTERVAL 1 MONTH) AND NOW() AND a.makeDate BETWEEN DATE_SUB(NOW(), INTERVAL 3 MONTH) AND NOW() AND a.makeDate BETWEEN DATE_SUB(NOW(), INTERVAL 6 MONTH) AND NOW() AND a.makeDate BETWEEN DATE_SUB(NOW(), INTERVAL 12 MONTH) AND NOW() AND a.makeDate < DATE_SUB(NOW(), INTERVAL 12 MONTH)
order by a.makeDate desc

mapper.update:
  	UPDATE product
	SET
		title = #{title}
	    ,prodType = #{prodType}
	    ,publisher = #{publisher}
	    ,makeDate = #{makeDate}
	    ,price = #{price}
	    ,sale = #{sale}
	    ,salePrice = #{salePrice}
	    ,point = #{point}
	    ,stockQty = #{stockQty}
	    ,intro = #{intro}
	    ,reviewNum = #{reviewNum}
	    ,shipDate = #{shipDate}
	    ,tableOfContents = #{tableOfContents}
	    ,publisherReview = #{publisherReview}
	    ,preivew = #{preivew}
	    ,bestNy = #{bestNy}
	    ,todayPickNy = #{todayPickNy}
	    ,sliderText = #{sliderText}
		,modDate = now()
	WHERE 1=1
		AND seq = #{seq}
